mapper.update:
update setmeal
category_id=#{categoryId}, name=#{name}, price=#{price}, image=#{image}, update_time=#{updateTime}, description=#{description}, status=#{status}
where id = #{id}

mapper.select:
select setmeal.*, category.name categoryName from setmeal left join category on setmeal.category_id = category.id
and setmeal.name like concat('%',#{name},'%') and setmeal.status=#{status} and setmeal.category_id=#{categoryId}
order by setmeal.create_time desc

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
